package main

import (
	"fmt"
	"strconv"
	"strings"
)

func min(x, y int) int {
	if x < y {
		return x
	}

	return y
}
func max(x, y int) int {
	if x < y {
		return y
	}

	return x
}

func main() {
	// input := "ULL\nRRDDD\nLURDL\nUUUUD"
	input := "\\\\"

	fmt.Println(puzzle1(input))
	fmt.Println(puzzle2(input))
}

func puzzle1(input string) string {
	keypad := [3][3]int{{1, 2, 3}, {4, 5, 6}, {7, 8, 9}}
	i, j := 1, 1
	code := strings.Builder{}

	for _, line := range strings.Split(input, "\n") {
		for _, m := range line {
			switch m {
			case 'U':
				i = max(0, i-1)
				break
			case 'D':
				i = min(len(keypad)-1, i+1)
				break
			case 'R':
				j = min(len(keypad[0])-1, j+1)
				break
			case 'L':
				j = max(0, j-1)
				break
			}
		}

		code.WriteString(strconv.Itoa(keypad[i][j]))
	}

	return code.String()
}

func puzzle2(input string) string {
	keypad := [5][5]string{
		{"", "", "1", "", ""},
		{"", "2", "3", "4", ""},
		{"5", "6", "7", "8", "9"},
		{"", "A", "B", "C", ""},
		{"", "", "D", "", ""},
	}
	i, j := 2, 0
	code := strings.Builder{}

	for _, line := range strings.Split(input, "\n") {
		for _, m := range line {
			switch m {
			case 'U':
				newI := max(0, i-1)
				if keypad[newI][j] != "" {
					i = newI
				}
				break
			case 'D':
				newI := min(len(keypad)-1, i+1)
				if keypad[newI][j] != "" {
					i = newI
				}
				break
			case 'R':
				newJ := min(len(keypad[0])-1, j+1)
				if keypad[i][newJ] != "" {
					j = newJ
				}
				break
			case 'L':
				newJ := max(0, j-1)
				if keypad[i][newJ] != "" {
					j = newJ
				}
				break
			}
		}

		code.WriteString(keypad[i][j])
	}

	return code.String()
}
